import React from "react";
import styled from "styled-components";

const Form = styled.form`
  margin-bottom: 50px;
  margin-left:30px;
  width: 100%;
`;

const Input = styled.input`
  all: unset;
  width: 100%;
  font-size: 28px;
`;

const SearchPresenter = ({movieResult, tvResult, searchTerm, loading, error, serchByTerm, updateTerm }) => (
  <Form onSubmit={serchByTerm}>
    <Input
      placeholder="Input Search Name"
      value={searchTerm}
      onChange={updateTerm}
    ></Input>
  </Form>
);

export default SearchPresenter;
